#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    vector<string> arr(n);    for (int i = 0; i < n; i++) cin >> arr[i];     string cur = "";     for (int i = 0; i < n; i++){        if (cur.empty()){            cur = arr[i];            continue;        }         string front = arr[i] + cur;        string back = cur + arr[i];         if (front <= back){            cur = front;        }        else{            cur = back;        }    }     cout << cur << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}