#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k; cin >> n >> k;    if(k & 1){        for(int i = 0; i < k; i++) cout << n << " "; cout << '\n';        return;    }     int tight = k;    int ans[k]{};    int bit = 31 - __builtin_clz(n);        for(int i = bit; i >= 0; i--){        if((n >> i) & 1){            int lv;            if(tight > 0){                lv = tight - 1;                tight--;            }            else lv = 0;             for(int j = 0; j < k; j++){                if(j == lv) continue;                ans[j] |= (1 << i);            }        }        else{            int rem = k - tight;            int up = rem - rem % 2;            for(int j = k - 1; up; j--, up--){                ans[j] |= (1 << i);            }        }    }     for(auto x : ans) cout << x << ' '; cout << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}