#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k; cin >> n >> k;    vector<int> a(n);    for(auto &i : a) cin >> i;    sort(all(a));    int mx = 0;    for(auto x : a) if(x == mx) mx++;    cout << min(mx, k - 1) << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}