#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, m, k; cin >> n >> m >> k;    int a = k - 1;    int b = n - k;    int l = min(a, b);    int r = max(a, b);    int L = (m + 1) / 3;    if(L > l) L = l;    //cout << L << endl;    int R = (m - L + 1) / 2;    if(R > r) R = r;    //cout << R << endl;    cout << L + R + 1 << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}