#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() int ans;void dfs(int u, int p, vector<vector<int>>& g, vector<int>& dep, vector<int>& cnt){    cnt[dep[u]]++;    int count = 0;    for(auto v : g[u]){        if(v == p) continue;        count++;        dep[v] = dep[u] + 1;        dfs(v, u, g, dep, cnt);    }    ans = max(ans, count + 1);} void solve(){    int n; cin >> n;    ans = 0;    vector<vector<int>> g(n + 1);    for(int i = 0; i < n - 1; i++){        int u, v; cin >> u >> v;        g[u].push_back(v);        g[v].push_back(u);    }    vector<int> dep(n + 1);    vector<int> cnt(n + 1);     dfs(1, 0, g, dep, cnt);    for(int i = 0; i <= n; i++){        ans = max(ans, cnt[i]);    }    cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}