#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int s, k, m; cin >> s >> k >> m;    int n = m / k;    int r = m % k;    int top = s;    if (n == 0){        cout << max(0LL, s - m) << "\n";        return;    }    int a = s - max(0LL, s - k);    int b = s - max(0LL, a - k);    if (n == 1)top = a;    else top = (n % 2 == 0 ? b : a);    if (r > 0) top = max(0LL, top - r);     cout << top << "\n";} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}