#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() unordered_map<int, int> dp; int yo(int x, int tar){    if(x == tar) return 0;    if(x < tar) return 1e9;    if(dp.count(x)) return dp[x];     int a = x / 2;    int b = (x + 1) / 2;    return dp[x] = 1 + min(yo(a, tar), yo(b, tar));} void solve(){    int n, k; cin >> n >> k;    int tar = k;    dp.clear();    int ans = yo(n, tar);    if(ans >= 1e9) ans = -1;    cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}