#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() int dp[35][35]; int yo(int n, int r) {    if (r > n || r < 0) return 0;    if (r == 0 || r == n) return 1;    int &ret = dp[n][r];    if (ret != -1) return ret;    return ret = yo(n - 1, r - 1) + yo(n - 1, r);} void solve() {    int n, k; cin >> n >> k;    int dep = 63 - __builtin_clzll(n);    memset(dp, -1, sizeof(dp));    int ans = (dep + 1 > k) ? 1 : 0;     for (int i = 1; i <= dep; i++) {        int x = i - 1;        int lo = max(0LL, k - i + 1);        for (int j = lo; j <= x; j++) ans += yo(x, j);    }    cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}