#include <bits/stdc++.h>using namespace std; #define int long long int#define all(x) x.begin(), x.end() int mex(vector<int> const& A) {    set<int> b(A.begin(), A.end());     int result = 0;    while (b.count(result))        ++result;    return result;} void solve(){    int n; cin >> n;    vector<int> a(n);    for(auto &i : a) cin >> i;    sort(all(a));    int ans = 1;    int cnt = 1;    for(int i = 0; i + 1 < n; i++){        if(a[i + 1] - a[i] == 1) cnt++;        else if(a[i] == a[i + 1]) continue;        else{            ans = max(ans, cnt);            cnt = 1;        }    }    ans = max(ans, cnt);    cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}