#include <bits/stdc++.h>using namespace std; #define int long long int#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    int a[n];    for(int i = 0; i < n; i++) cin >> a[i];    for (int i = 0; i < n - 1; ++i) {        if ((a[i] % 2) == (a[i+1] % 2)) {            cout << "NO" << "\n";            return;        }    }    cout << "YES\n";} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}