#include <bits/stdc++.h>using namespace std; #define int long long int#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    vector<int> a(n);    for(auto &i : a) cin >> i;    int x = count(all(a), 0);    int y = count(all(a), 1);    if(x == 0) {        cout << "NO\n";        return;    }    if(y == 0) {        if(x != 1) cout << "NO\n";        else cout << "YES\n";    }    else {        cout << "YES\n";    }} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}