#include <bits/stdc++.h>using namespace std; #define int long long int#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    string s; cin >> s;    int zero = count(all(s), '0');    map<int, int> mp;    for(int i = 0; i < n; i++){        if(i < zero and s[i] != '0') mp[i] = i + 1;        if(i >= zero and s[i] != '1') mp[i] = i + 1;    }    if(!mp.empty()){        cout << "Alice\n";        cout << mp.size() << '\n';        for(auto [f, s] : mp) cout << s << ' ';        cout << '\n';    }    else cout << "Bob\n";} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}