#include <bits/stdc++.h>using namespace std; #define int long long int#define all(x) x.begin(), x.end()const int mod = 998244353;  int n; string s; int dp1[105][105][4];int dp2[105][105][4]; int yo(int i, int bal, int st){    if(bal < 0 or bal > n) return 0;    if(i == n and (bal == 0 and st == 3)) return 1;    if(i == n) return 0;     if(dp1[i][bal][st] != -1) return dp1[i][bal][st];    //cout << "ways ->" << i << ", " << bal << ", " << st << '\n';     int ret = 0;    //nibo na    ret = (ret + yo(i + 1, bal, st)) % mod;     //nibo (    if(s[i] == '('){        int nst = st;        if(st == 1) nst = 2;        else if(st == 2) nst = 3;        else if(st == 3) nst = 3;         ret = (ret + yo(i + 1, bal + 1, nst)) % mod;    }     //nibo )    if(s[i] == ')' and bal > 0){        int nst = st;        if(st == 0) nst = 1;        ret = (ret + yo(i + 1, bal - 1, nst)) % mod;    }     return dp1[i][bal][st] = ret;} int yoo(int i, int bal, int st){    if(bal < 0 or bal > n) return 0;    if(i == n and (bal == 0 and st == 3)) return (mod - 2) % mod;    if(i == n) return 0;     if(dp2[i][bal][st] != -1) return dp2[i][bal][st];    //cout << "ways ->" << i << ", " << bal << ", " << st << '\n';     int ret = 0;    //nibo na    ret = (ret + yoo(i + 1, bal, st)) % mod;     //nibo (    if(s[i] == '('){        int nst = st;        if(st == 1) nst = 2;        else if(st == 2) nst = 3;        else if(st == 3) nst = 3;         int way = yo(i + 1, bal + 1, nst);        int sum = yoo(i + 1, bal + 1, nst);        ret = (ret + sum + way) % mod;    }     //nibo )    if(s[i] == ')' and bal > 0){        int nst = st;        if(st == 0) nst = 1;         int way = yo(i + 1, bal - 1, nst);        int sum = yoo(i + 1, bal - 1, nst);        ret = (ret + sum + way) % mod;    }     return dp2[i][bal][st] = ret;} void solve(){    cin >> n >> s;    memset(dp1, -1, sizeof(dp1));    memset(dp2, -1, sizeof(dp2));    yo(0, 0, 0);    int ans = yoo(0, 0, 0);    ans = (ans % mod + mod) % mod;    cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}