#include <bits/stdc++.h>using namespace std;#define int long long int#define all(x) x.begin(), x.end() struct grp {    int x, lowy, hiy; }; vector<grp> gr;int dp[200005][2]; int dis(int x1, int y1, int x2, int y2) {    return abs(x1 - x2) + abs(y1 - y2);} int yo(int i, int tp) {    if (i == 0) return 0;    if (dp[i][tp] != -1) return dp[i][tp];    int ans = 2e18;     int curX = gr[i].x;    int curL = gr[i].lowy;    int curH = gr[i].hiy;    int VD = curH - curL;     int prevX = gr[i - 1].x;    int prevH = gr[i - 1].hiy;    int prevL = gr[i - 1].lowy;    if (tp == 0) {         int one = yo(i - 1, 0) + dis(prevX, prevH, curX, curL);        int two = yo(i - 1, 1) + dis(prevX, prevL, curX, curL);        ans = min(one, two) + VD;    }    else {         int one = yo(i - 1, 0) + dis(prevX, prevH, curX, curH);        int two = yo(i - 1, 1) + dis(prevX, prevL, curX, curH);        ans = min(one, two) + VD;    }    return dp[i][tp] = ans; } void solve(){    int n, ax, ay, bx, by; cin >> n >> ax >> ay >> bx >> by;    map<int, pair<int,int>> mp;    mp[ax] = {ay, ay};    mp[bx] = {by, by};      vector<int> vx(n), vy(n);    for (auto& i : vx) cin >> i;    for (auto& i : vy) cin >> i;    for (int i = 0; i < n; i++) {        if (mp.find(vx[i]) == mp.end()) mp[vx[i]] = {vy[i], vy[i]};        else mp[vx[i]].first = min(mp[vx[i]].first, vy[i]), mp[vx[i]].second = max(mp[vx[i]].second, vy[i]);    }     gr.clear();    for (auto [f, s] : mp) gr.push_back({f, s.first, s.second});    int m = gr.size();    for (int i = 0; i <= m; i++) dp[i][0] = dp[i][1] = -1;    cout << yo(m - 1, 0) << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);     cin.tie(0);    int t;     cin >> t;    while (t--) solve();    return 0;}