#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, d; cin >> n >> d;    vector<pair<int, int>> vp;    for(int i = 0; i < n; i++){        int a, b; cin >> a >> b;        vp.push_back({a, b});    }     sort(vp.begin(), vp.end());    int ans = 0, sum = 0;    int l = 0;    for(int r = 0; r < n; r++){        sum += vp[r].second;        while(vp[r].first - vp[l].first >= d){            sum -= vp[l].second;            l++;        }        ans = max(ans, sum);    }     cout << ans <<'\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    //cin >> t;    while (t--)        solve();    return 0;}