#include <bits/stdc++.h>using namespace std; #define int long long int#define all(x) x.begin(), x.end() const int N = 500 + 10;int dp[N][N], a[N]; int yo(int l, int r){    if(l > r) return 0;    if(l == r) return 1;    int &ret = dp[l][r];    if(ret != -1) return ret;     ret = 1 + yo(l + 1, r);    if(a[l] == a[l + 1]) ret = min(ret, 1 + yo(l + 2, r));    for(int i = l + 2; i <= r; i++){        if(a[l] == a[i]){            ret = min(ret, yo(l + 1, i - 1) + yo(i + 1, r));        }    }    return ret;} void solve(){    int n; cin >> n;    for(int i = 0; i < n; i++) cin >> a[i];    memset(dp, -1, sizeof(dp));    cout << yo(0, n - 1) << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    //cin >> t;    while (t--)        solve();    return 0;}