#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() int n; vector<int> a;int dp[105][3]; int yo(int i, int last){    if(i == n) return 0;    if(dp[i][last] != -1) return dp[i][last];     int ret = yo(i + 1, 0);    if((a[i] == 1 or a[i] == 3) and last != 1) ret = max(ret, 1 + yo(i + 1, 1));    if((a[i] == 2 or a[i] == 3) and last != 2) ret = max(ret, 1 + yo(i + 1, 2));     return dp[i][last] = ret;}void solve(){    cin >> n;    a.resize(n);    for(int i = 0; i < n; i++) cin >> a[i];    memset(dp, -1, sizeof dp);     int mx = yo(0, 0);    cout << n - mx << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    //cin >> t;    while (t--)        solve();    return 0;}