#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() const int N = 1e5 + 5;const int inf = 2e18 + 5;int n; int c[N];string s[N][2];int dp[N][2]; int yo(int i, int j){    if(i == n) return 0;     int &ret = dp[i][j];    if(ret != -1) return ret;     int cost = inf;    if(s[i - 1][j] <= s[i][0]) cost = min(cost, yo(i + 1, 0));    if(s[i - 1][j] <= s[i][1]) cost = min(cost, c[i] + yo(i + 1, 1));     return ret = cost;} void solve(){    cin >> n;    for(int i = 0; i < n; i++) cin >> c[i];    for(int i = 0; i < n; i++){        string x; cin >> x;        s[i][0] = x;        reverse(all(x));        s[i][1] = x;    }    memset(dp, -1, sizeof dp);    int ans = min(yo(1, 0), c[0] + yo(1, 1));    cout << (ans == inf ? -1 : ans) << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    //cin >> t;    while (t--)        solve();    return 0;}